import React, {Component} from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';

class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      inputValue: '',
    }
  }

  checkHTML = (e) => {
    // e.preventDefault()
    let form = new FormData(this.form);
    let text = form.get('input');
    this.setState({inputValue: text})
    console.log(text)
    // return text;
  }

  render(){
    return (
      <div className="App">
        <Form className="form" onSubmit={this.checkHTML} ref={(el) => {this.form = el}}>
          <Form.Group>
            <Form.Control type="text" className="form-control" name="input" id="input" as="textarea" rows="3"/>
          </Form.Group>
          <Button type="submit">Okay</Button>
        </Form>
      </div>
    );
  }
}

export default App;
